settings.custom_css:
#logo {
    height: 180px;
    width: 490px;
}

@media screen and (max-width: 768px) {
    #logo {
        width: 210px;
        height: 100px;
    }
}

@media screen and (max-width: 460px) {
    #logo {
        width: 160px;
        height: 110px; 
    }
}

#brilha {
    transition: 1s all ease-in;
    animation: brilhar 0.8s infinite alternate-reverse;
}

@keyframes brilhar {
    to {
        color: cyan;
    }
}

@media screen and (max-width: 700px) {
#small {
    height: 150px;
    width: 150px;
    margin: 0 auto;
}}

#mensagem {
    background-color: yellow;
    font-size: 1em;
    position: fixed;
    padding: 5px 3em;
    top: 0;
    transition: all 1s ease-in;
    margin-top: 2em;
    margin-left: 0.4em;
}
